indexWeapon = 0
indexPattern = 1

flag = 0
activate = 1
timestart = 0
Wpattern = { ak47, galil, sg553, m4a4, m4a1, famas, aug }
Wkey1 = { ak47key, galilkey, sg553key, m4a4key, m4a1key, famaskey, augkey }
function OnEvent(event, arg)
    OutputLogMessage("\nEvent: " .. event .. " Arg: " .. arg .. " Activate: " .. activate .. "\n")
    --Hold the left mouse button
    if (event == "MOUSE_BUTTON_RELEASED") then
        indexPattern = 1
    end
    if (event == "MOUSE_BUTTON_PRESSED") then
        OutputLogMessage("\nMOUSE_BUTTON_PRESSED")
        EnablePrimaryMouseButtonEvents(true)

        if (arg == offkey) then
            indexWeapon = 0
            OutputLogMessage("\n OFF")
            activate = 0
        elseif (arg > 2) then
            ClearLog()
            activate = 1
            if(arg==Wkey1[1] and not IsModifierPressed("ctrl") and not IsModifierPressed("alt")) then
                indexWeapon=1
                OutputLogMessage("\n AK47")
            end
            if (arg == Wkey1[2] and IsModifierPressed("ctrl")) then
                indexWeapon = 2
                OutputLogMessage("\n GALIL")
            end
            if(arg==Wkey1[3] and IsModifierPressed("alt")) then
                indexWeapon=3
                OutputLogMessage("\n SG553")
            end
            if(arg==Wkey1[4] and not IsModifierPressed("ctrl") and not IsModifierPressed("alt")) then
                indexWeapon=4
                OutputLogMessage("\n M4A4")
            end
            if (arg == Wkey1[5] and IsModifierPressed("ctrl")) then
                indexWeapon = 5
                OutputLogMessage("\n M4A1")
            end
            if (arg == Wkey1[6]) then
                indexWeapon = 6
                OutputLogMessage("\n FAMAS")
            end
            if (arg == Wkey1[7] and IsModifierPressed("alt")) then
                indexWeapon = 7
                OutputLogMessage("\n AUG")
            end
            Sleep(100)
        end
        Move()
        OutputLogMessage("\nMOUSE_BUTTON_PRESSED END")

    end
end

function Move()
    OutputLogMessage("\nMove")
    while IsMouseButtonPressed(1) and activate == 1 do
        ClearLog()
        OutputLogMessage("\nwhile")
        if (indexWeapon == 17) then
            SetMKeyState(3)
            OutputLogMessage("\n break")
            break
        end
        if (indexWeapon > 0 and indexWeapon < 17) then
            if indexPattern < #Wpattern[indexWeapon] then
                if indexPattern == 1 then
                    timestart = GetRunningTime()
                end
                MoveMouseRelative(Wpattern[indexWeapon][indexPattern].x, Wpattern[indexWeapon][indexPattern].y)
                timestart = timestart + Wpattern[indexWeapon][indexPattern].d
                Sleep3(timestart)
                indexPattern = indexPattern + 1
            else
                ClearLog()
                OutputLogMessage("\n end")
                --activate = 0
            end
        end
    end
end
function Sleep3(time)
    while (time > GetRunningTime())
    do
    end
end